


exports.sum = function(a, b) {
  return a + b;
};

exports.multiply = function(a, b) {
  return a * b;
};

exports.slow_sum = function(a, b, wait, callback) {

  setTimeout(function() {
    callback(a + b);
  }, wait * 1000);

};

exports.slow_sum_gen = function*(a, b, wait) {

  yield new Promise(function(ack, reject) {

    setTimeout(function() {
     ack(a + b);
    }, wait * 1000);

  });

};

exports.slow_sum_gen_reject = function*(a, b) {

  yield new Promise(function(ack, reject) {

    setTimeout(function() {
     reject('non mi piaci');
    }, 1000);

  });

};

