'use strict';

const debug = require('debug')('corso:404');

module.exports = function () {
  return function *(next) {

    if (!this.body) {
      debug('%s:%s - not found', this.method, this.path);
      return this.throw(404, 'Not found!');
    }

    yield next;
  }
};
