
// guida: http://www.sandersdenardi.com/readable-writable-transform-streams-node/

var Transform = require('stream').Transform,
  util = require('util');

var TransformStream = function(options) {
  options = options || {};
  options.objectMode = false;
  Transform.call(this, options);
};

util.inherits(TransformStream, Transform);

TransformStream.prototype._transform = function(chunk, encoding, callback) {
  // console.log('chunk size: ' + chunk.length);

  //  if (typeof chunk.originalValue === 'undefined')
  //    chunk.originalValue = chunk.value;
  //  chunk.value++;
  chunk = new Buffer(chunk.toString().toUpperCase());

  this.push(chunk);
  callback();
};

module.exports = TransformStream;
